import React from 'react';
import SeriseCard from '../card/SeriesCard';
import axios from '../../../shared/util';
import {image_base_url} from '../../../shared/util';


export default ({match})=>{
	const [details, setDetails] = React.useState({})
	const [loading, setLoading] = React.useState(true)

	React.useEffect(()=>{
		axios.get(`/tv/${match.params.id}`)
			.then(resp => setDetails(resp.data))
			.finally(()=> setLoading(false))
	}, [])

	console.log(details)

	return(
		<div className="card bg-dark text-white">
			<img src={`${image_base_url}/w1920_and_h800_multi_faces${details.backdrop_path}`} alt='.' />
			<div className="row card-img-overlay">
				<div className="col-lg-3 col-md-4">
					<div className="w-100 d-flex justify-content-center">
						<SeriseCard/>
					</div>
				</div>
				<div className="col">
					<h3>Serise Name</h3>
					<span>Rating</span><br/>
					<span>Gener</span><br/>
					<span>Number of season</span><br/>
					<span>People likes</span><br/>
					<span>Actors</span><br/>
					<p>The Series big big big discription</p>
				</div>
			</div>
		</div>
	)
}
